mapper.select:
SELECT 

FROM cms_comment a

WHERE a.id = #{id}

SELECT 

FROM cms_comment a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

		AND a.content_id = #{contentId}


		AND a.member_id = #{memberId}




		ORDER BY ${page.orderBy}

SELECT 

FROM cms_comment a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE cms_comment SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
