mapper.select:
SELECT 

FROM cms_sensitive a

WHERE a.id = #{id}

SELECT 

FROM cms_sensitive a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

		AND a.title LIKE 
			'%'||#{title}||'%'
			'%'+#{title}+'%'
			concat('%',#{title},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM cms_sensitive a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE cms_sensitive SET 	
	title = #{title},
	update_by = #{updateBy.id},
	update_date = #{updateDate},
	remarks = #{remarks}
WHERE id = #{id}

UPDATE cms_sensitive SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
