mapper.select:
SELECT

FROM history a

WHERE a.id = #{id}

SELECT

FROM history a


    a.del_flag = #{DEL_FLAG_NORMAL}
    ${dataScope}



        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

SELECT

FROM history a


    a.del_flag = #{DEL_FLAG_NORMAL}
    ${dataScope}



        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

mapper.update:
UPDATE history SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
